#!/bin/bash

# Apply Modern Purple Hacker-Style Theme to OWASP Juice Shop
# This script modifies the Juice Shop application to include custom cyberpunk styling

set -euo pipefail

# Function to log errors and exit
error_exit() {
    echo "ERROR: $1" >&2
    exit 1
}

# Function to log success
log_success() {
    echo "âœ“ $1"
}

# Function to log info
log_info() {
    echo "â„¹ $1"
}

echo "=== Applying Modern Purple Hacker-Style Theme ==="

# Check if we're in the correct directory
if [ ! -f "app/package.json" ]; then
    error_exit "This script must be run from the project root directory."
fi

# Check if Juice Shop is cloned
if [ ! -d "app/views" ] || [ ! -d "app/routes" ]; then
    error_exit "OWASP Juice Shop not found. Please run './app/clone.sh' first."
fi

cd app

# Backup original files
log_info "Creating backups of original files..."
cp -r views views.backup 2>/dev/null || true
cp -r public public.backup 2>/dev/null || true

# Create custom public directory if it doesn't exist
mkdir -p public/css
mkdir -p public/js

# Copy our custom theme
log_info "Installing custom theme..."
cp ../app/custom-theme.css public/css/custom-theme.css

# Create theme injection script
cat > public/js/theme-injector.js << 'EOF'
(function() {
    'use strict';

    // Wait for DOM to load
    document.addEventListener('DOMContentLoaded', function() {
        // Inject custom CSS
        const link = document.createElement('link');
        link.rel = 'stylesheet';
        link.href = '/css/custom-theme.css';
        document.head.appendChild(link);

        // Add cyberpunk class to body
        document.body.classList.add('cyberpunk-theme');

        // Add security badges to key elements
        addSecurityBadges();

        // Add glitch effects to important elements
        addGlitchEffects();

        // Add matrix rain background (optional)
        addMatrixRain();

        // Override default styles after theme loads
        setTimeout(() => {
            applyThemeOverrides();
        }, 100);
    });

