            badge.className = 'security-badge pulse';
            badge.innerHTML = 'ðŸ”’ SECURE LOGIN';
            badge.style.cssText = `
                position: absolute;
                top: -10px;
                right: -10px;
                background: linear-gradient(45deg, #00FF88, #00D4FF);
                color: #0F0F23;
                padding: 5px 15px;
                border-radius: 20px;
                font-size: 0.8rem;
                font-weight: bold;
                text-transform: uppercase;
                letter-spacing: 1px;
                box-shadow: 0 0 10px #00FF88, 0 0 20px #00FF88, 0 0 40px #00FF88;
                z-index: 1000;
            `;
            form.style.position = 'relative';
            form.appendChild(badge);
        });

        // Add badges to product listings
        const products = document.querySelectorAll('.product-card, .thumbnail');
        products.forEach((product, index) => {
            if (index % 3 === 0) { // Add to every 3rd product for visual interest
                const badge = document.createElement('div');
                badge.className = 'security-badge';
                badge.innerHTML = 'âœ“ VERIFIED';
                badge.style.cssText = `
                    position: absolute;
                    top: 10px;
                    left: 10px;
                    background: linear-gradient(45deg, #8B5CF6, #A855F7);
                    color: white;
                    padding: 3px 10px;
                    border-radius: 15px;
                    font-size: 0.7rem;
                    font-weight: bold;
                    text-transform: uppercase;
                    letter-spacing: 1px;
                    box-shadow: 0 0 10px rgba(139, 92, 246, 0.5);
                    z-index: 100;
                `;
                product.style.position = 'relative';
                product.appendChild(badge);
            }
        });
    }

    function addGlitchEffects() {
        // Add glitch effect to main headings
        const headings = document.querySelectorAll('h1, h2, .navbar-brand');
        headings.forEach(heading => {
            heading.classList.add('glitch-text');
        });

        // Add terminal style to certain containers
        const containers = document.querySelectorAll('.card, .panel');
        containers.forEach(container => {
            if (Math.random() > 0.7) { // Add to 30% of containers randomly
                container.classList.add('terminal-style');
            }
        });
    }

